//  DATE HELPERS 
function addWorkdays(date, days) {
  let d = new Date(date), added = 0;
  while (added < days) { d.setDate(d.getDate() + 1); if (d.getDay() !== 0 && d.getDay() !== 6) added++; }
  return d;
}
function calcDeadlineFour(fe, ho, express) {
  // Express: siempre 2 das hbiles. Normal: siempre 5 das hbiles.
  return addWorkdays(new Date(`${fe}T${ho}`), express ? 2 : 5);
}
function calcDeadlineSubDiseno(fe, ho) { return addWorkdays(new Date(`${fe}T${ho}`), 5); }
function calcDeadlineSubProduccion(at) { return addWorkdays(new Date(at), 20); }
function fmtDate(d) { return new Date(d).toLocaleDateString("es-CL", { day: "2-digit", month: "short", year: "numeric" }); }
function fmtCurrency(v) { return "$" + Number(v || 0).toLocaleString("es-CL"); }
function dLeft(dl) {
  const t = new Date(); t.setHours(0, 0, 0, 0);
  const d = new Date(dl); d.setHours(0, 0, 0, 0);
  return Math.round((d - t) / 86400000);
}
function inPeriod(dateStr, period, from, to) {
  if (period === "todo") return true;
  const d = new Date(dateStr), now = new Date();
  if (period === "hoy") return d.toDateString() === now.toDateString();
  if (period === "semana") { const s = new Date(now); s.setDate(now.getDate() - 7); return d >= s; }
  if (period === "mes") return d.getMonth() === now.getMonth() && d.getFullYear() === now.getFullYear();
  if (period === "anio") return d.getFullYear() === now.getFullYear();
  if (period === "custom") {
    if (from && to) return d >= new Date(from) && d <= new Date(to);
    if (from) return d >= new Date(from);
    if (to) return d <= new Date(to);
  }
  return true;
}

