//  DEADLINE BADGE 
function DeadlineBadge({ dl }) {
  const days = dLeft(dl);
  let bg, color, text;
  if      (days < 0)   { bg = "#FEE2E2"; color = "#DC2626"; text = "VENCIDO"; }
  else if (days === 0) { bg = "#FEF3C7"; color = "#D97706"; text = "HOY"; }
  else if (days <= 2)  { bg = "#FEF3C7"; color = "#B45309"; text = `${days}d`; }
  else if (days <= 5)  { bg = "#FEF9C3"; color = "#854D0E"; text = `${days}d`; }
  else                 { bg = "#DCFCE7"; color = "#15803D"; text = `${days}d`; }
  return (
    <div style={{ textAlign: "right" }}>
      <div style={{ display: "inline-block", background: bg, color, fontWeight: 800, fontSize: 12, padding: "3px 10px", borderRadius: 6, letterSpacing: "0.03em" }}>{text}</div>
      <div style={{ fontSize: 12, color: "#6B7280", marginTop: 3, whiteSpace: "nowrap", fontFamily: "Montserrat, sans-serif" }}>Fecha Entrega: {fmtDate(dl)}</div>
    </div>
  );
}

