//  DONUT CHART (SVG) 
function DonutChart({ pending, delivered, size = 200 }) {
  const total = pending + delivered;
  if (total === 0) return (
    <div style={{ width: size, height: size, borderRadius: "50%", background: "#F3F4F6", display: "flex", alignItems: "center", justifyContent: "center" }}>
      <span style={{ fontSize: 12, color: "#D1D5DB", fontWeight: 700, letterSpacing: "0.06em" }}>SIN DATOS</span>
    </div>
  );
  const r = 72, cx = size / 2, cy = size / 2, stroke = 22;
  const circ = 2 * Math.PI * r;
  const dashDelivered = circ * (delivered / total);
  const dashPending   = circ * (pending / total);
  return (
    <div style={{ position: "relative", width: size, height: size, flexShrink: 0 }}>
      <svg width={size} height={size} style={{ transform: "rotate(-90deg)" }}>
        <circle cx={cx} cy={cy} r={r} fill="none" stroke="#F3F4F6" strokeWidth={stroke} />
        {delivered > 0 && (
          <circle cx={cx} cy={cy} r={r} fill="none" stroke="#059669" strokeWidth={stroke}
            strokeDasharray={`${dashDelivered} ${circ - dashDelivered}`} strokeDashoffset={0} strokeLinecap="butt" />
        )}
        {pending > 0 && (
          <circle cx={cx} cy={cy} r={r} fill="none" stroke="#3B82F6" strokeWidth={stroke}
            strokeDasharray={`${dashPending} ${circ - dashPending}`} strokeDashoffset={-dashDelivered} strokeLinecap="butt" />
        )}
      </svg>
    </div>
  );
}

