  const fS = data.sub.filter(o => check(o.createdAt));
  const fA = [...fF, ...fS];
  const fListo     = (data.listo     || []).filter(o => check(o.listoAt    || o.createdAt));
  const fDelivered = (data.delivered || []).filter(o => check(o.deliveredAt || o.createdAt));

