//  USUARIOS VIEW 
function UsuariosView({ currentUser }) {
  const [users, setUsers]       = useState([]);
  const [loading, setLoading]   = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editUser, setEditUser] = useState(null);   // null = new, object = editing
  const [form, setForm]         = useState({ name: "", email: "", password: "", role: "vendedor" });
  const [formError, setFormError] = useState("");
  const [saved, setSaved]       = useState(false);

  useEffect(() => {
    const u = loadUsers(); setUsers(u); setLoading(false);
  }, []);

  const openNew = () => {
    setForm({ name: "", email: "", password: "", role: "vendedor" });
    setFormError(""); setEditUser(null); setShowForm(true);
  };
  const openEdit = (u) => {
    setForm({ name: u.name, email: u.email, password: u.password, role: u.role });
    setFormError(""); setEditUser(u); setShowForm(true);
  };

  const handleSave = () => {
    setFormError("");
    if (!form.name.trim() || !form.email.trim() || !form.password.trim()) {
      setFormError("Todos los campos son obligatorios."); return;
    }
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(form.email)) { setFormError("Correo invlido."); return; }

    let updated;
    if (editUser) {
      updated = users.map(u => u.email === editUser.email ? { ...u, ...form } : u);
    } else {
      if (users.find(u => u.email.toLowerCase() === form.email.toLowerCase())) {
        setFormError("Ya existe un usuario con ese correo."); return;
      }
      updated = [...users, { ...form, email: form.email.toLowerCase() }];
    }
    saveUsers(updated);
    setUsers(updated);
    setShowForm(false);
    setSaved(true);
    setTimeout(() => setSaved(false), 2500);
  };

  const handleDelete = (email) => {
    if (email === currentUser.email) return;
    const updated = users.filter(u => u.email !== email);
    saveUsers(updated);
    setUsers(updated);
  };

  const roleLabel = r => r === "admin" ? "Admin" : "Vendedor";
  const roleBg    = r => r === "admin" ? { background: "#EDE9FE", color: "#6D28D9" } : { background: "#F3F4F6", color: "#6B7280" };

  if (loading) return <div style={{ padding: 40, color: "#9CA3AF", fontFamily: "Montserrat, sans-serif" }}>Cargando usuarios</div>;

  return (
    <div>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 24, flexWrap: "wrap", gap: 12 }}>
        <div>
          <h2 style={{ ...S.pageTitle, margin: 0 }}>Gestin de usuarios</h2>
          <p style={{ fontSize: 12, color: "#9CA3AF", margin: "4px 0 0", fontFamily: "Montserrat, sans-serif" }}>Solo los administradores pueden crear y modificar cuentas.</p>
        </div>
        <button style={S.btnPrimary} onClick={openNew}>+ Crear usuario</button>
      </div>

      {saved && (
        <div style={{ background: "#F0FDF4", border: "1px solid #BBF7D0", color: "#15803D", borderRadius: 8, padding: "10px 16px", fontSize: 13, fontWeight: 600, marginBottom: 16 }}>
           Cambios guardados correctamente.
        </div>
      )}

      {/* User table */}
      <div style={{ background: "#fff", borderRadius: 12, border: "1px solid #E5E7EB", overflow: "hidden" }}>
        {/* Header row */}
        <div style={{ display: "grid", gridTemplateColumns: "2fr 2fr 1fr 120px", gap: 0, background: "#F9FAFB", borderBottom: "1px solid #E5E7EB", padding: "10px 20px" }}>
          {["NOMBRE", "CORREO", "ROL", "ACCIONES"].map(h => (
            <span key={h} style={{ fontSize: 10, fontWeight: 700, color: "#9CA3AF", letterSpacing: "0.1em" }}>{h}</span>
          ))}
        </div>
        {users.length === 0 && (
          <div style={{ padding: "32px", textAlign: "center", color: "#D1D5DB", fontSize: 13 }}>Sin usuarios registrados</div>
        )}
        {users.map((u, i) => (
          <div key={u.email} style={{ display: "grid", gridTemplateColumns: "2fr 2fr 1fr 120px", gap: 0, padding: "14px 20px", borderBottom: i < users.length - 1 ? "1px solid #F3F4F6" : "none", alignItems: "center" }}>
            <div>
              <div style={{ fontWeight: 700, fontSize: 13, color: "#111827" }}>{u.name}</div>
              {u.email === currentUser.email && <div style={{ fontSize: 10, color: "#9CA3AF", marginTop: 2 }}> t</div>}
            </div>
            <div style={{ fontSize: 13, color: "#6B7280" }}>{u.email}</div>
            <div>
              <span style={{ ...S.chip, ...roleBg(u.role), fontSize: 10 }}>{roleLabel(u.role)}</span>
            </div>
            <div style={{ display: "flex", gap: 6 }}>
              <button onClick={() => openEdit(u)} style={{ ...S.actionBtn, background: "#F3F4F6", color: "#374151" }}> Editar</button>
              {u.email !== currentUser.email && (
                <button onClick={() => handleDelete(u.email)} style={{ ...S.actionBtn, background: "#FEF2F2", color: "#DC2626" }}></button>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Form modal */}
      {showForm && (
        <div style={S.overlay} onClick={e => e.target === e.currentTarget && setShowForm(false)}>
          <div style={{ ...S.confirmModal, maxWidth: 460 }}>
            <div style={{ ...S.confirmTitle, marginBottom: 20 }}>{editUser ? "Editar usuario" : "Crear nuevo usuario"}</div>
            <div style={{ display: "flex", flexDirection: "column", gap: 14 }}>
              <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 }}>
                <div style={{ gridColumn: "1 / -1" }}>
                  <label style={S.label}>Nombre completo *</label>
                  <input style={{ ...S.input, marginTop: 5 }} value={form.name} placeholder="Ej: Angelica Gonzalez" onChange={e => setForm({ ...form, name: e.target.value })} />
                </div>
                <div style={{ gridColumn: "1 / -1" }}>
                  <label style={S.label}>Correo electrnico *</label>
                  <input
                    style={{ ...S.input, marginTop: 5, background: editUser ? "#F9FAFB" : "#fff", color: editUser ? "#9CA3AF" : "#111827" }}
                    type="email" value={form.email}
                    placeholder="correo@foursport.cl"
                    readOnly={!!editUser}
                    onChange={e => !editUser && setForm({ ...form, email: e.target.value })} />
                  {editUser && <div style={{ fontSize: 10, color: "#9CA3AF", marginTop: 4 }}>El correo no se puede cambiar.</div>}
                </div>
                <div>
                  <label style={S.label}>Contrasea *</label>
                  <input style={{ ...S.input, marginTop: 5 }} type="text" value={form.password} placeholder="" onChange={e => setForm({ ...form, password: e.target.value })} />
                </div>
                <div>
                  <label style={S.label}>Rol *</label>
                  <select style={{ ...S.input, marginTop: 5 }} value={form.role} onChange={e => setForm({ ...form, role: e.target.value })}>
                    <option value="vendedor">Vendedor</option>
                    <option value="admin">Admin</option>
                  </select>
                </div>
              </div>
              {formError && (
                <div style={{ background: "#FEF2F2", color: "#DC2626", fontSize: 12, padding: "8px 12px", borderRadius: 8, fontWeight: 600 }}>{formError}</div>
              )}
            </div>
            <div style={{ ...S.confirmBtns, marginTop: 20 }}>
              <button style={S.btnGhost} onClick={() => setShowForm(false)}>Cancelar</button>
              <button style={S.btnPrimary} onClick={handleSave}>{editUser ? "GUARDAR CAMBIOS" : "CREAR USUARIO"}</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

